// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

interface IAuthRegistry {
    function isAuthorized(address user) external view returns (bool);
}

contract Proposals {
    struct Proposal {
        string titleCid;
        string shortDescriptionCid;
        string descriptionInDetailsCid;
        uint256 yesVotes;
        uint256 noVotes;
        address creator;
        bool activeStatus;
        uint256 expiredDate;
        uint256 categoryId;
        uint256 createdAt;
        uint256 updatedAt;
        mapping(address => bool) hasVotedYes;
        mapping(address => bool) hasVotedNo;
    }

    IAuthRegistry public authRegistry;
    uint256 public proposalCount;
    mapping(uint256 => Proposal) private proposals;
    mapping(address => uint256) public lastCreatedAt;

    // Events
    event ProposalCreated(
        uint256 indexed proposalId, 
        address indexed creator, 
        string titleCid, 
        string shortDescriptionCid, 
        string descriptionInDetailsCid,
        uint256 categoryId,
        uint256 expiredDate
    );
    event ProposalVotedYes(uint256 indexed proposalId, address indexed voter);
    event ProposalVotedNo(uint256 indexed proposalId, address indexed voter);
    event ProposalStatusChanged(uint256 indexed proposalId, bool activeStatus);
    event ProposalExpired(uint256 indexed proposalId, uint256 expiredAt);

    constructor(address _authRegistry) {
        require(_authRegistry != address(0), "Auth registry address cannot be zero");
        authRegistry = IAuthRegistry(_authRegistry);
    }

    // Only authorized users can perform write operations
    modifier onlyAuthorized() {
        require(authRegistry.isAuthorized(msg.sender), "User not authorized");
        _;
    }

    // User can only create one proposal per day to prevent spam


    // Only proposal creator can modify
    modifier onlyCreator(uint256 proposalId) {
        Proposal storage p = proposals[proposalId];
        require(msg.sender == p.creator, "Only creator can modify proposal");
        _;
    }

    // Check if proposal is active and not expired
    modifier onlyActiveProposal(uint256 proposalId) {
        Proposal storage p = proposals[proposalId];
        require(p.activeStatus, "Proposal is not active");
        require(block.timestamp < p.expiredDate, "Proposal has expired");
        _;
    }

    /**
     * Create a new proposal
     * @param titleCid IPFS CID for proposal title
     * @param shortDescriptionCid IPFS CID for short description
     * @param descriptionInDetailsCid IPFS CID for detailed description
     * @param categoryId Category ID for the proposal
     * @param expiredDate Timestamp when proposal expires
     * @return proposalId The ID of the created proposal
     */
    function createProposal(
        string calldata titleCid,
        string calldata shortDescriptionCid,
        string calldata descriptionInDetailsCid,
        uint256 categoryId,
        uint256 expiredDate
    ) external onlyAuthorized returns (uint256) {
        require(bytes(titleCid).length > 0, "Title CID cannot be empty");
        require(bytes(shortDescriptionCid).length > 0, "Short description CID cannot be empty");
        require(bytes(descriptionInDetailsCid).length > 0, "Detailed description CID cannot be empty");
        require(expiredDate > block.timestamp, "Expired date must be in the future");
        require(categoryId > 0, "Category ID must be valid");

        proposalCount++;
        uint256 proposalId = proposalCount;

        Proposal storage p = proposals[proposalId];
        p.titleCid = titleCid;
        p.shortDescriptionCid = shortDescriptionCid;
        p.descriptionInDetailsCid = descriptionInDetailsCid;
        p.creator = msg.sender;
        p.activeStatus = true;
        p.yesVotes = 0;
        p.noVotes = 0;
        p.categoryId = categoryId;
        p.expiredDate = expiredDate;
        p.createdAt = block.timestamp;
        p.updatedAt = block.timestamp;

        lastCreatedAt[msg.sender] = block.timestamp;

        emit ProposalCreated(
            proposalId, 
            msg.sender, 
            titleCid, 
            shortDescriptionCid, 
            descriptionInDetailsCid,
            categoryId,
            expiredDate
        );
        return proposalId;
    }

    /**
     * Vote YES on a proposal
     * @param proposalId The ID of the proposal to vote on
     */
    function voteYes(uint256 proposalId) external onlyAuthorized onlyActiveProposal(proposalId) {
        Proposal storage p = proposals[proposalId];
        require(p.creator != address(0), "Proposal does not exist");
        require(!p.hasVotedYes[msg.sender], "You have already voted YES on this proposal");
        require(p.creator != msg.sender, "Cannot vote on your own proposal");

        // Remove NO vote if exists
        if (p.hasVotedNo[msg.sender]) {
            p.hasVotedNo[msg.sender] = false;
            p.noVotes--;
        }

        p.hasVotedYes[msg.sender] = true;
        p.yesVotes++;
        p.updatedAt = block.timestamp;

        emit ProposalVotedYes(proposalId, msg.sender);
    }

    /**
     * Vote NO on a proposal
     * @param proposalId The ID of the proposal to vote on
     */
    function voteNo(uint256 proposalId) external onlyAuthorized onlyActiveProposal(proposalId) {
        Proposal storage p = proposals[proposalId];
        require(p.creator != address(0), "Proposal does not exist");
        require(!p.hasVotedNo[msg.sender], "You have already voted NO on this proposal");
        require(p.creator != msg.sender, "Cannot vote on your own proposal");

        // Remove YES vote if exists
        if (p.hasVotedYes[msg.sender]) {
            p.hasVotedYes[msg.sender] = false;
            p.yesVotes--;
        }

        p.hasVotedNo[msg.sender] = true;
        p.noVotes++;
        p.updatedAt = block.timestamp;

        emit ProposalVotedNo(proposalId, msg.sender);
    }

    /**
     * Remove vote from a proposal
     * @param proposalId The ID of the proposal to remove vote from
     */
    function removeVote(uint256 proposalId) external onlyAuthorized onlyActiveProposal(proposalId) {
        Proposal storage p = proposals[proposalId];
        require(p.creator != address(0), "Proposal does not exist");

        bool hadVote = false;

        if (p.hasVotedYes[msg.sender]) {
            p.hasVotedYes[msg.sender] = false;
            p.yesVotes--;
            hadVote = true;
        }

        if (p.hasVotedNo[msg.sender]) {
            p.hasVotedNo[msg.sender] = false;
            p.noVotes--;
            hadVote = true;
        }

        require(hadVote, "You have not voted on this proposal");
        p.updatedAt = block.timestamp;
    }

    /**
     * Change proposal status (activate/deactivate)
     * @param proposalId The ID of the proposal
     * @param newStatus New active status
     */
    function changeProposalStatus(uint256 proposalId, bool newStatus) external onlyAuthorized onlyCreator(proposalId) {
        Proposal storage p = proposals[proposalId];
        require(p.creator != address(0), "Proposal does not exist");
        require(p.activeStatus != newStatus, "Status is already set to this value");

        p.activeStatus = newStatus;
        p.updatedAt = block.timestamp;

        emit ProposalStatusChanged(proposalId, newStatus);
    }

    /**
     * Update proposal expiration date (only extend, not reduce)
     * @param proposalId The ID of the proposal
     * @param newExpiredDate New expiration timestamp
     */
    function extendProposalDeadline(uint256 proposalId, uint256 newExpiredDate) external onlyAuthorized onlyCreator(proposalId) {
        Proposal storage p = proposals[proposalId];
        require(p.creator != address(0), "Proposal does not exist");
        require(newExpiredDate > p.expiredDate, "Can only extend deadline, not reduce it");
        require(newExpiredDate > block.timestamp, "New deadline must be in the future");

        p.expiredDate = newExpiredDate;
        p.updatedAt = block.timestamp;
    }

    /**
     * Manually expire a proposal (only by creator)
     * @param proposalId The ID of the proposal to expire
     */
    function expireProposal(uint256 proposalId) external onlyAuthorized onlyCreator(proposalId) {
        Proposal storage p = proposals[proposalId];
        require(p.creator != address(0), "Proposal does not exist");
        require(p.activeStatus, "Proposal is already inactive");

        p.activeStatus = false;
        p.updatedAt = block.timestamp;

        emit ProposalExpired(proposalId, block.timestamp);
    }

    /**
     * Get proposal details
     * @param proposalId The ID of the proposal
     * @return titleCid IPFS CID for title
     * @return shortDescriptionCid IPFS CID for short description
     * @return descriptionInDetailsCid IPFS CID for detailed description
     * @return yesVotes Number of YES votes
     * @return noVotes Number of NO votes
     * @return creator Address of the creator
     * @return activeStatus Whether the proposal is active
     * @return expiredDate Expiration timestamp
     * @return categoryId Category ID
     * @return createdAt Timestamp when created
     * @return updatedAt Timestamp when last updated
     */
    function getProposal(uint256 proposalId) external view returns (
        string memory titleCid,
        string memory shortDescriptionCid,
        string memory descriptionInDetailsCid,
        uint256 yesVotes,
        uint256 noVotes,
        address creator,
        bool activeStatus,
        uint256 expiredDate,
        uint256 categoryId,
        uint256 createdAt,
        uint256 updatedAt
    ) {
        Proposal storage p = proposals[proposalId];
        require(p.creator != address(0), "Proposal does not exist");
        
        return (
            p.titleCid,
            p.shortDescriptionCid,
            p.descriptionInDetailsCid,
            p.yesVotes,
            p.noVotes,
            p.creator,
            p.activeStatus,
            p.expiredDate,
            p.categoryId,
            p.createdAt,
            p.updatedAt
        );
    }

    /**
     * Check if user has voted on a proposal
     * @param proposalId The ID of the proposal
     * @param user Address of the user
     * @return hasVotedYes Whether user has voted YES
     * @return hasVotedNo Whether user has voted NO
     */
    function getUserVote(uint256 proposalId, address user) external view returns (
        bool hasVotedYes,
        bool hasVotedNo
    ) {
        Proposal storage p = proposals[proposalId];
        require(p.creator != address(0), "Proposal does not exist");
        
        return (p.hasVotedYes[user], p.hasVotedNo[user]);
    }

    /**
     * Get proposal voting statistics
     * @param proposalId The ID of the proposal
     * @return yesVotes Number of YES votes
     * @return noVotes Number of NO votes
     * @return netVotes Net votes (yesVotes - noVotes)
     * @return totalVotes Total votes (yesVotes + noVotes)
     * @return yesPercentage Percentage of YES votes
     */
    function getProposalVotes(uint256 proposalId) external view returns (
        uint256 yesVotes,
        uint256 noVotes,
        int256 netVotes,
        uint256 totalVotes,
        uint256 yesPercentage
    ) {
        Proposal storage p = proposals[proposalId];
        require(p.creator != address(0), "Proposal does not exist");
        
        yesVotes = p.yesVotes;
        noVotes = p.noVotes;
        netVotes = int256(yesVotes) - int256(noVotes);
        totalVotes = yesVotes + noVotes;
        yesPercentage = totalVotes > 0 ? (yesVotes * 100) / totalVotes : 0;
        
        return (yesVotes, noVotes, netVotes, totalVotes, yesPercentage);
    }

    /**
     * Get all proposals created by a user
     * @param user Address of the user
     * @return proposalIds Array of proposal IDs created by the user
     */
    function getProposalsByUser(address user) external view returns (uint256[] memory proposalIds) {
        uint256[] memory tempIds = new uint256[](proposalCount);
        uint256 count = 0;
        
        for (uint256 i = 1; i <= proposalCount; i++) {
            if (proposals[i].creator == user) {
                tempIds[count] = i;
                count++;
            }
        }
        
        proposalIds = new uint256[](count);
        for (uint256 i = 0; i < count; i++) {
            proposalIds[i] = tempIds[i];
        }
        
        return proposalIds;
    }

    /**
     * Get active proposals by category
     * @param categoryId The category ID to filter by
     * @return proposalIds Array of active proposal IDs in the category
     */
    function getActiveProposalsByCategory(uint256 categoryId) external view returns (uint256[] memory proposalIds) {
        uint256[] memory tempIds = new uint256[](proposalCount);
        uint256 count = 0;
        
        for (uint256 i = 1; i <= proposalCount; i++) {
            if (proposals[i].categoryId == categoryId && 
                proposals[i].activeStatus && 
                block.timestamp < proposals[i].expiredDate) {
                tempIds[count] = i;
                count++;
            }
        }
        
        proposalIds = new uint256[](count);
        for (uint256 i = 0; i < count; i++) {
            proposalIds[i] = tempIds[i];
        }
        
        return proposalIds;
    }

    /**
     * Get expired proposals
     * @return proposalIds Array of expired proposal IDs
     */
    function getExpiredProposals() external view returns (uint256[] memory proposalIds) {
        uint256[] memory tempIds = new uint256[](proposalCount);
        uint256 count = 0;
        
        for (uint256 i = 1; i <= proposalCount; i++) {
            if (block.timestamp >= proposals[i].expiredDate) {
                tempIds[count] = i;
                count++;
            }
        }
        
        proposalIds = new uint256[](count);
        for (uint256 i = 0; i < count; i++) {
            proposalIds[i] = tempIds[i];
        }
        
        return proposalIds;
    }

    /**
     * Check if proposal is expired
     * @param proposalId The ID of the proposal
     * @return isExpired Whether the proposal is expired
     */
    function isProposalExpired(uint256 proposalId) external view returns (bool isExpired) {
        Proposal storage p = proposals[proposalId];
        require(p.creator != address(0), "Proposal does not exist");
        
        return block.timestamp >= p.expiredDate;
    }
}

contracts/report/reports.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

interface IAuthRegistry {
    function isAuthorized(address user) external view returns (bool);
}

contract Reports {
    struct Report {
        string titleCid;
        string descriptionCid;
        string evidenceHashCid;
        uint256 upvotes;
        uint256 downvotes;
        address creator;
        bool resolved;
        address assignedTo;
        uint256 createdAt;
        uint256 resolvedAt;
        mapping(address => bool) hasUpvoted;
        mapping(address => bool) hasDownvoted;
    }

    IAuthRegistry public authRegistry;
    uint256 public reportCount;
    mapping(uint256 => Report) private reports;
    mapping(address => uint256) public lastCreatedAt;

    // Events
    event ReportCreated(
        uint256 indexed reportId, 
        address indexed creator, 
        string titleCid, 
        string descriptionCid, 
        string evidenceHashCid
    );
    event ReportUpvoted(uint256 indexed reportId, address indexed voter);
    event ReportDownvoted(uint256 indexed reportId, address indexed voter);
    event ReportResolved(uint256 indexed reportId, address indexed resolver, uint256 resolvedAt);
    event ReportAssigned(uint256 indexed reportId, address indexed assignedTo);

    constructor(address _authRegistry) {
        require(_authRegistry != address(0), "Auth registry address cannot be zero");
        authRegistry = IAuthRegistry(_authRegistry);
    }

    // Only authorized users can perform write operations
    modifier onlyAuthorized() {
        require(authRegistry.isAuthorized(msg.sender), "User not authorized");
        _;
    }

    // User can only create one report per day to prevent spam
    modifier onlyOncePerDay() {
        require(
            block.timestamp >= lastCreatedAt[msg.sender] + 1 days,
            "You can only create one report per day"
        );
        _;
    }

    // Only report creator or assigned person can resolve
    modifier onlyCreatorOrAssigned(uint256 reportId) {
        Report storage r = reports[reportId];
        require(
            msg.sender == r.creator || msg.sender == r.assignedTo,
            "Only creator or assigned person can resolve"
        );
        _;
    }

    /**
     * Create a new report
     * @param titleCid IPFS CID for report title
     * @param descriptionCid IPFS CID for report description
     * @param evidenceHashCid IPFS CID for evidence hash
     * @return reportId The ID of the created report
     */
    function createReport(
        string calldata titleCid,
        string calldata descriptionCid,
        string calldata evidenceHashCid
    ) external onlyAuthorized onlyOncePerDay returns (uint256) {
        require(bytes(titleCid).length > 0, "Title CID cannot be empty");
        require(bytes(descriptionCid).length > 0, "Description CID cannot be empty");
        require(bytes(evidenceHashCid).length > 0, "Evidence hash CID cannot be empty");

        reportCount++;
        uint256 reportId = reportCount;

        Report storage r = reports[reportId];
        r.titleCid = titleCid;
        r.descriptionCid = descriptionCid;
        r.evidenceHashCid = evidenceHashCid;
        r.creator = msg.sender;
        r.resolved = false;
        r.upvotes = 0;
        r.downvotes = 0;
        r.createdAt = block.timestamp;
        r.resolvedAt = 0;

        lastCreatedAt[msg.sender] = block.timestamp;

        emit ReportCreated(reportId, msg.sender, titleCid, descriptionCid, evidenceHashCid);
        return reportId;
    }

    /**
     * Upvote a report
     * @param reportId The ID of the report to upvote
     */
    function upvoteReport(uint256 reportId) external onlyAuthorized {
        Report storage r = reports[reportId];
        require(r.creator != address(0), "Report does not exist");
        require(!r.resolved, "Cannot vote on resolved report");
        require(!r.hasUpvoted[msg.sender], "You have already upvoted this report");
        require(r.creator != msg.sender, "Cannot vote on your own report");

        // Remove downvote if exists
        if (r.hasDownvoted[msg.sender]) {
            r.hasDownvoted[msg.sender] = false;
            r.downvotes--;
        }

        r.hasUpvoted[msg.sender] = true;
        r.upvotes++;

        emit ReportUpvoted(reportId, msg.sender);
    }

    /**
     * Downvote a report
     * @param reportId The ID of the report to downvote
     */
    function downvoteReport(uint256 reportId) external onlyAuthorized {
        Report storage r = reports[reportId];
        require(r.creator != address(0), "Report does not exist");
        require(!r.resolved, "Cannot vote on resolved report");
        require(!r.hasDownvoted[msg.sender], "You have already downvoted this report");
        require(r.creator != msg.sender, "Cannot vote on your own report");

        // Remove upvote if exists
        if (r.hasUpvoted[msg.sender]) {
            r.hasUpvoted[msg.sender] = false;
            r.upvotes--;
        }

        r.hasDownvoted[msg.sender] = true;
        r.downvotes++;

        emit ReportDownvoted(reportId, msg.sender);
    }

    /**
     * Remove vote from a report
     * @param reportId The ID of the report to remove vote from
     */
    function removeVote(uint256 reportId) external onlyAuthorized {
        Report storage r = reports[reportId];
        require(r.creator != address(0), "Report does not exist");
        require(!r.resolved, "Cannot change vote on resolved report");

        bool hadVote = false;

        if (r.hasUpvoted[msg.sender]) {
            r.hasUpvoted[msg.sender] = false;
            r.upvotes--;
            hadVote = true;
        }

        if (r.hasDownvoted[msg.sender]) {
            r.hasDownvoted[msg.sender] = false;
            r.downvotes--;
            hadVote = true;
        }

        require(hadVote, "You have not voted on this report");
    }

    /**
     * Assign report to someone for resolution
     * @param reportId The ID of the report to assign
     * @param assignTo Address to assign the report to
     */
    function assignReport(uint256 reportId, address assignTo) external onlyAuthorized {
        Report storage r = reports[reportId];
        require(r.creator != address(0), "Report does not exist");
        require(!r.resolved, "Cannot assign resolved report");
        require(assignTo != address(0), "Cannot assign to zero address");
        require(authRegistry.isAuthorized(assignTo), "Assigned person must be authorized");

        r.assignedTo = assignTo;

        emit ReportAssigned(reportId, assignTo);
    }

    /**
     * Mark report as resolved
     * @param reportId The ID of the report to resolve
     */
    function resolveReport(uint256 reportId) external onlyAuthorized onlyCreatorOrAssigned(reportId) {
        Report storage r = reports[reportId];
        require(r.creator != address(0), "Report does not exist");
        require(!r.resolved, "Report already resolved");

        r.resolved = true;
        r.resolvedAt = block.timestamp;

        emit ReportResolved(reportId, msg.sender, block.timestamp);
    }

    /**
     * Reopen a resolved report (only by creator)
     * @param reportId The ID of the report to reopen
     */
    function reopenReport(uint256 reportId) external onlyAuthorized {
        Report storage r = reports[reportId];
        require(r.creator != address(0), "Report does not exist");
        require(r.resolved, "Report is not resolved");
        require(msg.sender == r.creator, "Only creator can reopen report");

        r.resolved = false;
        r.resolvedAt = 0;
    }

    /**
     * Get report details
     * @param reportId The ID of the report
     * @return titleCid IPFS CID for title
     * @return descriptionCid IPFS CID for description
     * @return evidenceHashCid IPFS CID for evidence hash
     * @return upvotes Number of upvotes
     * @return downvotes Number of downvotes
     * @return creator Address of the creator
     * @return resolved Whether the report is resolved
     * @return assignedTo Address assigned to resolve the report
     * @return createdAt Timestamp when created
     * @return resolvedAt Timestamp when resolved (0 if not resolved)
     */
    function getReport(uint256 reportId) external view returns (
        string memory titleCid,
        string memory descriptionCid,
        string memory evidenceHashCid,
        uint256 upvotes,
        uint256 downvotes,
        address creator,
        bool resolved,
        address assignedTo,
        uint256 createdAt,
        uint256 resolvedAt
    ) {
        Report storage r = reports[reportId];
        require(r.creator != address(0), "Report does not exist");
        
        return (
            r.titleCid,
            r.descriptionCid,
            r.evidenceHashCid,
            r.upvotes,
            r.downvotes,
            r.creator,
            r.resolved,
            r.assignedTo,
            r.createdAt,
            r.resolvedAt
        );
    }

    /**
     * Check if user has voted on a report
     * @param reportId The ID of the report
     * @param user Address of the user
     * @return hasUpvoted Whether user has upvoted
     * @return hasDownvoted Whether user has downvoted
     */
    function getUserVote(uint256 reportId, address user) external view returns (
        bool hasUpvoted,
        bool hasDownvoted
    ) {
        Report storage r = reports[reportId];
        require(r.creator != address(0), "Report does not exist");
        
        return (r.hasUpvoted[user], r.hasDownvoted[user]);
    }

    /**
     * Get report voting statistics
     * @param reportId The ID of the report
     * @return upvotes Number of upvotes
     * @return downvotes Number of downvotes
     * @return netVotes Net votes (upvotes - downvotes)
     * @return totalVotes Total votes (upvotes + downvotes)
     */
    function getReportVotes(uint256 reportId) external view returns (
        uint256 upvotes,
        uint256 downvotes,
        int256 netVotes,
        uint256 totalVotes
    ) {
        Report storage r = reports[reportId];
        require(r.creator != address(0), "Report does not exist");
        
        upvotes = r.upvotes;
        downvotes = r.downvotes;
        netVotes = int256(upvotes) - int256(downvotes);
        totalVotes = upvotes + downvotes;
        
        return (upvotes, downvotes, netVotes, totalVotes);
    }

    /**
     * Get all reports created by a user
     * @param user Address of the user
     * @return reportIds Array of report IDs created by the user
     */
    function getReportsByUser(address user) external view returns (uint256[] memory reportIds) {
        uint256[] memory tempIds = new uint256[](reportCount);
        uint256 count = 0;
        
        for (uint256 i = 1; i <= reportCount; i++) {
            if (reports[i].creator == user) {
                tempIds[count] = i;
                count++;
            }
        }
        
        reportIds = new uint256[](count);
        for (uint256 i = 0; i < count; i++) {
            reportIds[i] = tempIds[i];
        }
        
        return reportIds;
    }

    /**
     * Get reports assigned to a user
     * @param user Address of the user
     * @return reportIds Array of report IDs assigned to the user
     */
    function getReportsAssignedTo(address user) external view returns (uint256[] memory reportIds) {
        uint256[] memory tempIds = new uint256[](reportCount);
        uint256 count = 0;
        
        for (uint256 i = 1; i <= reportCount; i++) {
            if (reports[i].assignedTo == user && !reports[i].resolved) {
                tempIds[count] = i;
                count++;
            }
        }
        
        reportIds = new uint256[](count);
        for (uint256 i = 0; i < count; i++) {
            reportIds[i] = tempIds[i];
        }
        
        return reportIds;
    }
}

